export function getQuickSortAnimations(items) {
  const animations = [];
  if (items.length <= 1) return animations;
  quickSort(items,0,items.length-1);

  return animations;

} 

function swap(items, leftIndex, rightIndex){
    var temp = items[leftIndex];
    items[leftIndex] = items[rightIndex];
    items[rightIndex] = temp;
}
function partition(items, left, right) {
    var pivot = items[right], i = left, j = right;
    var ptr=left; 

    while (i <right) {
        if(items[i]<pivot){ swap(items,i,ptr); ptr++;  }
        i++;
    }
    swap(items,ptr,right)
    return ptr;
}

function quickSort(items, left, right) {

    if(left>=right){ return; }
    var index = partition(items, left, right);
    quickSort(items, left, index - 1);
    quickSort(items, index+1, right);   
}
