import React from 'react';
import {getMergeSortAnimations} from '../sortingAlgorithms/sortingAlgorithms.js';
import {getHeapSortAnimations} from '../sortingAlgorithms/heapSort.js';
import './SortingVisualizer.css';
import Navbar from 'react-bootstrap/Navbar'
import NavDropdown from 'react-bootstrap/NavDropdown'
import Nav from 'react-bootstrap/Nav'

// Change this value for the speed of the animations.
const ANIMATION_SPEED_MS = 2;

// Change this value for the number of bars (value) in the array.
const NUMBER_OF_ARRAY_BARS = 290;

// This is the main color of the array bars.
const PRIMARY_COLOR = 'turquoise';

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = 'red';
const THIRD_COLOR = 'pink';
const FOURTH_COLOR = 'blue';

const bar_width=20;
const bar_margin=2

export default class SortingVisualizer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      array: [],
    };
  }

  componentDidMount() {
    this.resetArray();
  }



  resetArray() {
    const array = [];
    var No_arr=(0.7*(window.screen.width))/(bar_width+2*bar_margin);
    for (let i = 0; i < No_arr; i++) {
      var ht=0.7*((window.screen.height)*(window.devicePixelRatio));
      array.push(randomIntFromInterval(5, ht));
    }
    const arraybars = document.getElementsByClassName('array-bar');
    arraybars.backgroundColor='red';
    this.setState({array});
  }

  AjustArray()
  {
      var Slider = document.getElementsByClassName('slider');
      this.bar_width=50-Slider.value;
      this.resetArray();
  }

  mergeSort() {

    const animations = getMergeSortAnimations(this.state.array);
    console.log(this.state.array);
    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName('array-bar');
      const isColorChange = i % 3 !== 2;
      if (isColorChange) {
        const [barOneIdx, barTwoIdx] = animations[i];
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;
        const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
        setTimeout(() => {
          barOneStyle.backgroundColor = color;
          barTwoStyle.backgroundColor = color;
        }, i * ANIMATION_SPEED_MS);
      } else {
        setTimeout(() => {
          const [barOneIdx, newHeight] = animations[i];
          const barOneStyle = arrayBars[barOneIdx].style;
          barOneStyle.height = `${newHeight}px`;
        }, i * ANIMATION_SPEED_MS);
      }
    }
  }

  quickSort() {
    // We leave it as an exercise to the viewer of this code to implement this method.
  }

  heapSort() {

    const animations = getHeapSortAnimations(this.state.array);
    console.log(this.state.array);

    for (let i = 0; i < animations.length; i++) {
      const arrayBars = document.getElementsByClassName('array-bar');
      const [barOneIdx, barTwoIdx] = animations[i];

      const rem = i % 3;

      if (barOneIdx!=-1) {
        const barOneStyle = arrayBars[barOneIdx].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;

        if(rem!=1)
        {
            const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
            setTimeout(() => {
            barOneStyle.backgroundColor = color;
            barTwoStyle.backgroundColor = color;
          }, i * ANIMATION_SPEED_MS);
        }else{

            setTimeout(() => {
            const temp1=barOneStyle.height;
            const temp2=barTwoStyle.height;
            barOneStyle.height=temp2;
            barTwoStyle.height=temp1;
          }, i * ANIMATION_SPEED_MS);

        }      
      } else {
        const barOneStyle = arrayBars[0].style;
        const barTwoStyle = arrayBars[barTwoIdx].style;

        if(rem==0)
        {
          setTimeout(() => {
            barOneStyle.backgroundColor = THIRD_COLOR;
            barTwoStyle.backgroundColor = THIRD_COLOR;
          }, i * ANIMATION_SPEED_MS);
        }else if(rem==2){

          setTimeout(() => {
            barOneStyle.backgroundColor = PRIMARY_COLOR;
            barTwoStyle.backgroundColor = FOURTH_COLOR;
          }, i * ANIMATION_SPEED_MS);

        }else{

          setTimeout(() => {
            const temp1=barOneStyle.height;
            const temp2=barTwoStyle.height;
            barOneStyle.height=temp2;
            barTwoStyle.height=temp1;
          }, i * ANIMATION_SPEED_MS);

        } 

      }
    }

  }

  bubbleSort() {
    // We leave it as an exercise to the viewer of this code to implement this method.
  }

  // NOTE: This method will only work if your sorting algorithms actually return
  // the sorted arrays; if they return the animations (as they currently do), then
  // this method will be broken.
  testSortingAlgorithms() {
    for (let i = 0; i < 100; i++) {
      const array = [];
      const length = randomIntFromInterval(1, 1000);
      for (let i = 0; i < length; i++) {
        array.push(randomIntFromInterval(-1000, 1000));
      }
      const javaScriptSortedArray = array.slice().sort((a, b) => a - b);
      const mergeSortedArray = getMergeSortAnimations(array.slice());
      console.log(arraysAreEqual(javaScriptSortedArray, mergeSortedArray));
    }
  }

  render() {
    const {array} = this.state;

    return (
      <div className="main-container">
        <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Navbar.Brand href="#home">Sorting-Visualizer</Navbar.Brand>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link  onClick={() => this.resetArray()} >Generate new Array</Nav.Link>
                <label>Adjust Array size</label>
                  <input type="range" className="slider" min="5" max="50" step="5" 
                  value="13"
                  onChange={event => this.AjustArray()} >
                  </input>
              </Nav>
              <Nav>
                <Nav.Link onClick={() => this.mergeSort()}>Merge-Sort</Nav.Link>
                <Nav.Link onClick={() => this.heapSort()}>Heap-Sort</Nav.Link>
                <Nav.Link onClick={() => this.quickSort()}>Quick-Sort</Nav.Link>
                <Nav.Link onClick={() => this.bubbleSort()}>Bubble-Sort</Nav.Link>            
              </Nav>
            </Navbar.Collapse>
          </Navbar>

        <div className="array-container">
          {array.map((value, idx) => (
            <div
              className="array-bar"
              key={idx}
              style={{
                backgroundColor: PRIMARY_COLOR,
                height: `${value}px`,
                width: `${bar_width}px`,
              }}></div>
          ))}
          {/*<button onClick={() => this.testSortingAlgorithms()}>
            Test Sorting Algorithms (BROKEN)
          </button>
          */}
        </div>
      </div>
    );
  }
}

// From https://stackoverflow.com/questions/4959975/generate-random-number-between-two-numbers-in-javascript
function randomIntFromInterval(min, max) {
  // min and max included
  return Math.floor(Math.random() * (max - min + 1) + min);
}

function arraysAreEqual(arrayOne, arrayTwo) {
  if (arrayOne.length !== arrayTwo.length) return false;
  for (let i = 0; i < arrayOne.length; i++) {
    if (arrayOne[i] !== arrayTwo[i]) {
      return false;
    }
  }
  return true;
}
